import React from "react";
import Header from "./partials/Header";
import Todo from "./partials/Todo";
import AddTodoModal from "./partials/AddTodoModal";
import { useEffect, useState } from "react";
import { getToken } from "../services/api";
import { useNavigate } from "react-router-dom";
import { getTodoListApi } from "../services/api";
import { ToastContainer, toast } from "react-toastify";

function Home() {
  const navigation = useNavigate();
  const [list, setList] = useState([]);
  const [refreshList, setRefreshList] = useState();
  useEffect(() => {
    if (!getToken()) {
      navigation("/login");
    }
    fetchTodoList();
  }, [refreshList]);

  async function fetchTodoList() {
    const result = await getTodoListApi();
    console.log("todolist", result);
    if (result.status == 200 && result.data.status === 200) {
      setList(result.data.data.todos);
    }
  }
  return (
    <div>
      <Header />
      <ToastContainer />
      <div className="container">
        <div className="row justify-content-md-center mt-4">
          {list.map((todo) => (
            <Todo todo={todo} key={todo._id} setRefreshList={setRefreshList} />
          ))}
        </div>
      </div>

      <div
        className=""
        style={{ position: "fixed", right: 50, bottom: 50, zIndex: 1030 }}
      >
        <AddTodoModal setRefreshList={setRefreshList} />
      </div>
    </div>
  );
}

export default Home;
