import React from "react";
import moment from "moment/moment";
import { ToastContainer, toast } from "react-toastify";
import { deleteTodoApi } from "../../services/api.js";
function Todo({ todo }) {

  const handleDelete = async () => {
    const result = await deleteTodoApi({
      todoId: todo._id
    });
    console.log("todo is deleted",result);
    if (result.data.status === 200) {
      toast("Deleted");
    } else {
      toast("Failed to delete,please try again");
    }
  };
  return (
    <div className="col-sm-3 mx-3 my-2 alert bg-light">
      <div className="card-header">
        {todo.isCompleted ? "Completed" : "Not Completed"}
      </div>
      <div className="card-body">
        <h4 className="card-title">{todo.desc}</h4>
        <p className="card-text">{moment(todo.date).fromNow()}</p>
        <ToastContainer />
      </div>
      <div
        className="actionButtons"
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <div className="deleteButton">
          <button style={{ background: "IndianRed" }} onClick={handleDelete}>
            
            Delete
          </button>
        </div>
        <div className="markTodo">
          <button style={{ background: "MediumSeaGreen" }}>
            {todo.isCompleted ? "Mark Uncomplete" : "Mark Complete"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Todo;
