import React from "react";
import moment from "moment/moment";
import { ToastContainer, toast } from "react-toastify";
import { deleteTodoApi,MarkTodoApi } from "../../services/api.js";
function Todo(props) {

  const handleDelete = async (_id) => {
    console.log(_id);
    const result = await deleteTodoApi({
      todo_id: _id
    });
    
    if (result.data.status === 200) {
      props.setRefreshList(new Date())
      toast("Deleted");
    } else {
      toast("Failed to delete,please try again");
    }
  };
  const handleMarkTodo = async(_id)=>{
    const result = await MarkTodoApi({
      todo_id: _id
    });

    if (result.data.status === 200) {
      props.setRefreshList(new Date())
      toast(result.data.message);
    } else {
      toast("Failed to mark,please try again");
    }
  };
  
  return (
    <div className="col-sm-4 mx-4 my-3 alert bg-light">
      <div className="card-header">
        {/* {props?.isCompleted ? "Completed" : "Not Completed"} */}
      </div>
      <div className="card-body">
        <h4 className="card-title">{props?.desc}</h4>
        <p className="card-text">{moment(props?.date).fromNow()}</p>
       
      </div>
      
      <div
        className="actionButtons"
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <div className="deleteButton">
        <div className="deleteButton">
          <button style={{ background: "IndianRed" }} onClick={()=> {handleDelete(props._id)}}>
            Delete
          </button>
        </div>
        <div className="markTodo">
          <button onClick ={()=> {handleMarkTodo(props._id)}} style={{background: props?.isCompleted ? "SeaGreen" : " "  }}>
            {props?.isCompleted ? "Done!!!" : "Mark Complete"}
          </button>
        </div>
      </div>
      </div>

      
    </div>
  );
}

export default Todo;
